$(document).ready(function(){var userId=common.user_data().cuserInfoid;var source="userId"+userId;var sign=md5(source+"key"+common.secretKey()).toUpperCase();$('.fruitMall_wrap_content_left').on('click',function(){window.location.href='fruit_exchange.html'});$('.fruitMall_wrap_content_right').on('click',function(){window.location.href='fruit_get.html'});$('.header_left').on('click',function(){window.location.href='my.html'});function get_fruit_money(userId,sign,source){$.ajax({url:common.http,type:"post",dataType:"jsonp",data:{method:'user_score',userId:userId,tokenId:common.tokenId(),sign:sign,source:source},success:function(data){if(data.statusCode="100000"){$(".fruitMall_wrap_top_left span").html(data.data.score)}else{$(".fruitMall_wrap_top_left span").html("0")}},error:function(data){common.prompt(data.statusStr)}})};get_fruit_money(userId,sign,source)});